import React, { useEffect, useState, useRef } from 'react';
import './styles.css';
import { PALETTE, contrastColor, formatNumberForDisplay } from './utils';
import { Box, Button, TextField, Select, MenuItem, Dialog, DialogTitle, DialogContent, DialogActions, IconButton, Typography } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import UploadFileIcon from '@mui/icons-material/UploadFile';
import DownloadIcon from '@mui/icons-material/Download';

const stateKey = 'timeoff_planner_v3_react';

function defaultData(year){
  return {
    year,
    categories: [
      { id:'cat_pto', name:'PTO', qty:15, used:0, color:PALETTE[0]},
      { id:'cat_sick', name:'Sick', qty:10, used:0, color:PALETTE[4]},
    ],
    events: {}
  };
}

export default function App(){
  const today = new Date();
  const [data, setData] = useState(()=> {
    const raw = localStorage.getItem(stateKey);
    if(raw) try{ return JSON.parse(raw); }catch(e){ console.warn(e); }
    return defaultData(today.getFullYear());
  });
  const [yearSelect, setYearSelect] = useState(data.year);
  const [catName, setCatName] = useState('');
  const [catQty, setCatQty] = useState('');
  const [chosenColor, setChosenColor] = useState(PALETTE[0]);
  const [paletteOpen, setPaletteOpen] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);
  const [modalCtx, setModalCtx] = useState(null);
  const fileInputRef = useRef(null);
  const dragCatRef = useRef(null);

  useEffect(()=> {
    localStorage.setItem(stateKey, JSON.stringify(data));
  }, [data]);

  useEffect(()=> {
    // keep year select in sync
    setYearSelect(data.year);
  }, [data.year]);

  function save(newData){
    setData(newData);
    localStorage.setItem(stateKey, JSON.stringify(newData));
  }

  function countUsed(catId){
    let sum = 0;
    Object.values(data.events).forEach(ev => {
      if(ev && ev.catId === catId) sum += (ev.half ? 0.5 : 1);
    });
    return sum;
  }

  function updateUsed(newData){
    newData.categories.forEach(c => c.used = countUsed(c.id));
  }

  function addCategory(){
    const name = catName.trim();
    const qty = parseFloat(catQty) || 0;
    if(!name || qty <= 0){ alert('Please provide a name and quantity > 0'); return; }
    const id = 'cat_' + Date.now() + '_' + Math.floor(Math.random()*9999);
    const next = { ...data, categories: [...data.categories, { id, name, qty, used:0, color:chosenColor }] };
    updateUsed(next);
    save(next);
    setCatName(''); setCatQty(''); setChosenColor(PALETTE[0]);
  }

  function deleteCategory(catId){
    const next = { ...data, categories: data.categories.filter(c=>c.id!==catId)};
    for(const day in next.events) if(next.events[day] && next.events[day].catId === catId) delete next.events[day];
    updateUsed(next); save(next);
  }

  function onDrop(date, e){
    e.preventDefault();
    const catId = e.dataTransfer.getData('text/plain') || (dragCatRef.current && dragCatRef.current.id);
    if(!catId) return;
    const cat = data.categories.find(c=>c.id===catId);
    if(!cat) return;
    const used = countUsed(cat.id);
    if(used + 1 > cat.qty + 0.001){ alert('No remaining days in this category'); return; }
    const next = { ...data, events: {...data.events, [date]: { catId: cat.id, half: false } } };
    updateUsed(next); save(next);
  }

  function onDragStartCat(e, cat){
    dragCatRef.current = cat;
    e.dataTransfer.setData('text/plain', cat.id);
    const canvas = document.createElement('canvas'); canvas.width=64; canvas.height=64;
    const ctx = canvas.getContext('2d'); ctx.fillStyle = cat.color; ctx.fillRect(0,0,64,64);
    ctx.fillStyle = contrastColor(cat.color); ctx.font='bold 18px sans-serif'; ctx.textAlign='center'; ctx.textBaseline='middle';
    ctx.fillText(cat.name[0]||'•', 32,32);
    const img = new Image(); img.src = canvas.toDataURL('image/png');
    img.onload = ()=> {
      try{ e.dataTransfer.setDragImage(img, 32,32); }catch(err){}
    };
  }

  function openDayModal(date){
    setModalCtx({ type:'day', date, ev: data.events[date] || null });
    setModalOpen(true);
  }

  function applyModalChanges(ctx){
    if(!ctx) return;
    const date = ctx.date;
    const half = !!ctx.half;
    const selectedSwap = ctx.swapTo;
    const next = { ...data, events: {...data.events} };
    if(selectedSwap){
      next.events[date] = { catId: selectedSwap, half };
    } else {
      const ev = next.events[date];
      if(ev && ev.catId){
        ev.half = half;
      }
    }
    updateUsed(next); save(next); setModalOpen(false);
  }

  function exportJSON(){
    const blob = new Blob([JSON.stringify(data,null,2)], {type:'application/json'});
    const a = document.createElement('a'); a.href = URL.createObjectURL(blob); a.download = `timeoff-${data.year}.json`; a.click();
  }

  function importJSONFile(files){
    const f = files[0]; if(!f) return;
    const reader = new FileReader();
    reader.onload = ev => {
      try{
        const obj = JSON.parse(ev.target.result);
        if(obj && obj.categories){
          updateUsed(obj);
          setData(obj);
        } else alert('Invalid file');
      }catch(err){ alert('Invalid JSON'); }
    };
    reader.readAsText(f);
  }

  function clearAll(){
    if(window.confirm('Clear all events?')){
      const next = {...data, events: {}};
      updateUsed(next); save(next);
    }
  }

  // render calendar
  function renderCalendar(){
    const year = data.year;
    const months = [];
    for(let m=0;m<12;m++){
      const first = new Date(year,m,1).getDay();
      const daysInMonth = new Date(year,m+1,0).getDate();
      const cells = [];
      for(let i=0;i<first;i++) cells.push({ empty: true });
      for(let d=1; d<=daysInMonth; d++){
        const date = `${year}-${String(m+1).padStart(2,'0')}-${String(d).padStart(2,'0')}`;
        const ev = data.events[date];
        cells.push({ date, day:d, ev });
      }
      months.push({ m, name: new Date(year,m,1).toLocaleString(undefined,{month:'long'}), cells });
    }
    return months;
  }

  const months = renderCalendar();

  return (
    <Box className="app">
      <Box className="navbar">
        <Typography variant="h6">Time Off Planner</Typography>

        <Typography sx={{mt:2}} className="label">Year</Typography>
        <Select value={data.year} onChange={(e)=>{ const y = +e.target.value; const next = {...data, year: y}; setData(next); setYearSelect(y); save(next); }}>
          {Array.from({length:7}).map((_,i)=> {
            const y = data.year - 3 + i;
            return <MenuItem key={y} value={y}>{y}</MenuItem>;
          })}
        </Select>

        <Typography className="label" sx={{mt:2}}>Create category</Typography>
        <TextField label="Category name (e.g. PTO)" size="small" value={catName} onChange={(e)=>setCatName(e.target.value)} />
        <TextField label="Quantity (days)" size="small" type="number" value={catQty} onChange={(e)=>setCatQty(e.target.value)} sx={{mt:1}} />

        <Typography className="label" sx={{mt:2}}>Pick color</Typography>
        <Box sx={{display:'flex', gap:1, alignItems:'center', flexWrap:'wrap'}}>
          <Button variant="outlined" size="small" onClick={()=>setPaletteOpen(p=>!p)} className="small-btn">Choose color ▾</Button>
          {paletteOpen && <Box sx={{display:'flex', gap:1, mt:1, flexWrap:'wrap'}} role="menu">
            {PALETTE.map(col=>(
              <Box key={col} className="color-swatch" sx={{width:36,height:36,background:col, cursor:'pointer', outline: chosenColor===col ? '3px solid rgba(255,255,255,0.12)' : 'none'}} onClick={()=>{ setChosenColor(col); setPaletteOpen(false); }} />
            ))}
          </Box>}
        </Box>

        <Box sx={{display:'flex',gap:1,mt:2}}>
          <Button variant="contained" startIcon={<AddIcon />} onClick={addCategory}>Add Category</Button>
          <Button variant="outlined" startIcon={<DownloadIcon />} onClick={exportJSON}>Export</Button>
          <Button variant="outlined" startIcon={<UploadFileIcon />} onClick={()=>fileInputRef.current && fileInputRef.current.click()}>Import</Button>
          <input ref={fileInputRef} type="file" accept="application/json" style={{display:'none'}} onChange={(e)=> importJSONFile(e.target.files)} />
        </Box>

        <Typography className="label" sx={{mt:2}}>Categories</Typography>
        <Box id="categories">
          {data.categories.map(cat=>{
            const used = countUsed(cat.id);
            const remaining = Math.max(0, cat.qty - used);
            return (
              <Box key={cat.id} className="category" draggable onDragStart={(e)=>onDragStartCat(e, cat)} onClick={()=> {
                // open editor
                const name = prompt('Category name', cat.name);
                if(name === null) return;
                const qty = parseFloat(prompt('Quantity (days)', String(cat.qty)));
                if(!isNaN(qty)) {
                  const next = {...data, categories: data.categories.map(c=> c.id===cat.id ? {...c, name, qty} : c)};
                  updateUsed(next); save(next);
                }
              }}>
                <Box sx={{width:38,height:38, borderRadius:1, background:cat.color}} />
                <Box className="meta" sx={{flex:1}}>
                  <div className="name">{cat.name}</div>
                  <div className="qty">{formatNumberForDisplay(remaining, cat.used)}/{formatNumberForDisplay(cat.qty, cat.used)} remaining</div>
                </Box>
                <Box className="actions">
                  <Button size="small" onClick={(e)=>{ e.stopPropagation(); if(window.confirm('Delete category and remove its events?')) deleteCategory(cat.id); }} className="small-btn">Delete</Button>
                </Box>
              </Box>
            );
          })}
        </Box>

        <div className="footer-note">Drag a category square to any day. Click a filled day to remove, swap, or convert to half-day.</div>
        <Box sx={{mt:2}} >
          <Button variant="outlined" className="small-btn" onClick={clearAll}>Clear All</Button>
        </Box>
      </Box>

      <Box className="calendar">
        <Box className="year-grid">
          {months.map(month => (
            <Box key={month.m} className="month">
              <h3 style={{textAlign:'center', margin:'2px 0 8px'}}>{month.name} {data.year}</h3>
              <div className="weekdays">{['S','M','T','W','T','F','S'].map(d=> <div key={d}>{d}</div>)}</div>
              <div className="days">
                {month.cells.map((cell, i) => {
                  if(cell.empty) return <div key={i} className="day inactive" />;
                  const ev = cell.ev;
                  const bg = ev && ev.catId ? (data.categories.find(c=>c.id===ev.catId)?.color || 'transparent') : 'transparent';
                  const date = cell.date;
                  return (
                    <div key={date} className={'day' + (ev ? ' filled' : '')} onDragOver={(e)=>e.preventDefault()} onDrop={(e)=>onDrop(date,e)} onClick={()=> openDayModal(date)} style={{background: bg}}>
                      <div className="date-num" style={{color: ev ? contrastColor(bg): undefined}}>{cell.day}</div>
                    </div>
                  );
                })}
              </div>
            </Box>
          ))}
        </Box>
      </Box>

      <Dialog open={modalOpen} onClose={()=>setModalOpen(false)}>
        <DialogTitle>Day options</DialogTitle>
        <DialogContent>
          {modalCtx && (
            <DayModalContent ctx={modalCtx} data={data} onChange={(newCtx)=>setModalCtx(newCtx)} />
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={()=>setModalOpen(false)}>Close</Button>
          <Button onClick={()=>{ applyModalChanges(modalCtx); }}>Save</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

function DayModalContent({ ctx, data, onChange }){
  const ev = ctx.ev;
  const [swapTo, setSwapTo] = useState(ev ? ev.catId : '');
  const [half, setHalf] = useState(ev ? !!ev.half : false);

  useEffect(()=> {
    onChange({...ctx, swapTo, half});
  }, [swapTo, half]);

  return (
    <div style={{minWidth:360}}>
      <div style={{marginBottom:8}}>
        {ev && ev.catId ? (
          <div style={{display:'flex',gap:8,alignItems:'center'}}>
            <div style={{width:24,height:24,borderRadius:6,background: data.categories.find(x=>x.id===ev.catId)?.color}} />
            <strong>{data.categories.find(x=>x.id===ev.catId)?.name}</strong> {ev.half ? '(Half day)' : ''}
          </div>
        ) : <div>No time off on this day</div>}
      </div>

      <div style={{marginBottom:8}}>
        <select value={swapTo} onChange={(e)=>setSwapTo(e.target.value)} style={{width:'100%',padding:8,borderRadius:8}}>
          <option value=''>-- Swap to another category --</option>
          {data.categories.map(c=> <option key={c.id} value={c.id}>{c.name} ({(c.qty - c.used).toFixed(1)} left)</option>)}
        </select>
      </div>

      <div style={{display:'flex',gap:8,marginBottom:8}}>
        <button className={'seg-btn' + (!half ? ' selected' : '')} onClick={()=>setHalf(false)}>Full day</button>
        <button className={'seg-btn' + (half ? ' selected' : '')} onClick={()=>setHalf(true)}>Half day</button>
      </div>

      <div style={{marginTop:8}}>
        <Button variant="outlined" onClick={()=>{
          if(window.confirm('Remove time off for this day?')){
            const next = {...data, events: {...data.events}};
            delete next.events[ctx.date];
            next.categories.forEach(c=> c.used = 0);
            // recompute used:
            Object.values(next.events).forEach(ev=> { if(ev && ev.catId){
              const cat = next.categories.find(c=>c.id===ev.catId);
              if(cat) cat.used += (ev.half?0.5:1);
            }});
            localStorage.setItem(stateKey, JSON.stringify(next));
            window.location.reload();
          }
        }}>Remove day</Button>
      </div>
    </div>
  );
}
